import React, { Component } from "react";

import classes from "./WorkComponent.module.css";
import "../../../../node_modules/@fortawesome/fontawesome-free/css/all.css";
import { CopyToClipboard } from "react-copy-to-clipboard";

class WorkComponent extends Component {
    //   state = {
    //     copied: false,
    //     delete: false,
    //   };
    constructor(props) {
        super(props);
        this.state = {
            copied: false,
            clicked: false
        }
        this.handleClick = this.handleClick.bind(this);

    }

    handleClick() {
        this.setState({
            delete: !this.state.delete
        })
        // if (!this.state.clicked) {
        //     document.body.classList.add('rel');
        // } else {
        //     document.body.classList.remove('rel');
        // }
    }

    render() {
        return (
            <li className={classes.Work}>
                <div className={classes.TopWorkHeader}>
                    <div className={classes.RemoveWork} onClick={this.handleClick}>
                        <i class="fas fa-trash-alt" ></i>
                    </div>
                    <div className={classes.Label}>{this.props.name}</div>
                </div>
                <div className={classes.Description}>
                    <style>
                        @import
                        url('https://fonts.googleapis.com/css2?family=Langar&family=Pacifico&family=Questrial&display=swap');
          </style>
                    {this.props.description}
                </div>

                <div className={classes.Password}>
                    <style>
                        @import
                        url('https://fonts.googleapis.com/css2?family=Andika+New+Basic:ital@1&display=swap');
          </style>
                    <div>password:</div>
                    <div className={classes.PasswordFont}>{this.props.password}</div>
                    <CopyToClipboard onCopy={this.props.copyFunc} text="zabastovka2002">
                        <div className={classes.clipBoard}>
                            <i class={"far fa-copy"}></i>
                        </div>
                    </CopyToClipboard>
                </div>
                <div className={classes.Users}>
                    <div className={classes.UserGroup}>
                        <i class="fa fa-users" aria-hidden="true">
                            :
            </i>
                        <font className={classes.UsersGroupValue}>5</font>
                    </div>
                    <div className={classes.UserGroup}>
                        <i class="fas fa-user-tie">:</i>
                        <font className={classes.UsersGroupValue}>5</font>
                    </div>
                    <div className={classes.UserGroup}>
                        <i class="fas fa-hospital-user">:</i>
                        <font className={classes.UsersGroupValue}>5</font>
                    </div>
                </div>
                <div className={this.state.delete ? [classes.active, classes.DeleteContainer].join(" ") : classes.DeleteContainer}>

                        <div className={classes.container}>
                            <h1>Delete Work</h1>
                            <p>Are you sure you want to delete your work?</p>

                            <div className={classes.clearfix}>
                                <button type="button" onClick={this.handleClick} className={classes.cancelbtn}>Cancel</button>
                                <button type="button" onClick={this.handleClick} className={classes.deletebtn} >Delete</button>
                            </div>
                        </div>

                </div>
                {/* <div id="id01" class="modal">
                    <span onclick={this.handleClick} class="close" title="Close Modal">×</span>
                    <form class="modal-content" action="/action_page.php">
                        <div class="container">
                            <h1>Delete Account</h1>
                            <p>Are you sure you want to delete your account?</p>

                            <div class="clearfix">
                                <button type="button" onclick={this.handleClick} class="cancelbtn">Cancel</button>
                                <button type="button" onclick={this.handleClick} class="deletebtn">Delete</button>
                            </div>
                        </div>
                    </form>
                </div> */}
            </li>
        );
    }
}
export default WorkComponent;
