import React, { Component } from "react";
import * as actions from "../../store/actions/index";
import { connect } from "react-redux";
import WorkComponent from "../../components/Works/WorkComponent/WorkComponent";
import classes from "./Works.module.css";

class MyWorks extends Component {
  state = { works: [] };
  getSnapshotBeforeUpdate(prevProps, prevState) {
    // Добавляются ли в список новые элементы?
    // Запоминаем значение прокрутки, чтобы использовать его позже.
    if (prevProps.works.length < this.props.works.length) {

      return list.scrollHeight - list.scrollTop;
    }
    return null;
  }
  componentDidMount() {
    this.props.onUploadWorks(this.props.userId, this.props.token);
    this.setState({ works: this.props.works });
    console.log(this.state.works);
  }

  render() {
    let worksRender = this.state.works.map((work, id) => {
      return (
        <WorkComponent
          key={id}
          description={work.description}
          name={work.name}
          password={work.password}
          workId={work.id}
        />
      );
    });
    return (
      <React.Fragment>
        <ul className={classes.WorkList}>{worksRender}</ul>
      </React.Fragment>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    token: state.auth.token,
    userId: state.auth.userId,
    error: state.auth.error,
    redirect: state.auth.redirect,
    loading: state.auth.loading,
    works: state.works.works,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    onUploadWorks: (userId, token) =>
      dispatch(actions.uploadWorks(userId, token)),
    setRedirectPath: (path) => dispatch(actions.setRedirectPath(path)),
  };
};
export default connect(mapStateToProps, mapDispatchToProps)(MyWorks);
